(function() {
  'use strict';

  // Define Modules
  angular.module('todo.form');
  angular.module('todo.shared');

  angular.module('todo', [
    'todo.form',
    'todo.shared'
  ]);
} ());

(function(app) {
  'use strict';

  var ListCtrl = function(users) {
    this.usersService = users;

    this.initialize();
  };

  ListCtrl.prototype.initialize = function() {
    // Setting title
    this.title = 'AngularJS - The cool way';

    // Setting users
    this.users = this.usersService.get();
  };

  ListCtrl.prototype.saveUser = function() {
    this.usersService.post(this.newUser);
    this.newUser = undefined;
  };

  ListCtrl.$inject = ['todo.shared.services.users'];

  app.controller('todo.form.controllers.List', ListCtrl);
} (angular.module('todo.form')));

(function(app) {
  'use strict';

  var users = function() {
    this.list = [{
      name: 'Mauricio',
      age: 24
    }, {
      name: 'Weslley',
      age: 20
    }];
  };

  users.prototype.get = function() {
    return this.list;
  };

  users.prototype.post = function(data) {
    this.list.push(data);
    return this.list;
  };

  app.service('todo.shared.services.users', users);

} (angular.module('todo.shared')));
